$(function(){var b=$(".kanban .sortable-list"),g,c,e,f,a,d=$(".message");
d.hide();b.sortable({connectWith:".sortable-list",opacity:0.6,placeholder:"placeholder",forcePlaceholderSize:true,cancel:".disabled",help:"clone",items:"li",start:function(h,i){$(this).sortable("refresh");
e=i.item;c=g=i.item.parent();},change:function(h,i){if(i.sender){c=i.placeholder.parent();}},stop:function(){f=c.find("li").length;a=g.find("li").length;
c.parent().find(".badge").html(f);g.parent().find(".badge").html(a);d.fadeIn(200);d.find(".msg").html("Moved <strong>"+e.text()+"</strong> from <strong>"+g.parent().attr("id")+"</strong> to <strong>"+c.parent().attr("id")+"</strong>");
},update:function(h,i){}}).disableSelection();});